mapper.select:
select * from (select ROW_NUMBER() over (order by member_num) as rn,
from members
MEMBER_NAME like '%' || #{searchWord} || '%' or member_id like '%' || #{searchWord} || '%' or member_num like '%' || #{searchWord} || '%'
)sub1 where rn between #{startRow} and #{endRow}

mapper.insert:
insert into members(
	member_num
	, member_id
	, member_pw
	, member_name
	, member_addr
	, member_addr_detail
	, member_post
	, member_phone1
	, member_phone2
	, member_regist
	, member_birth
	, gender
	, member_email
	, member_email_conf
	, point
 ) values(
	#{memberNum}
	, #{memberId}
	, #{memberPw}
	, #{memberName}
	, #{memberAddr}
	, #{memberAddrDetail}
	, #{memberPost}
	, #{memberPhone1}
	, #{memberPhone2}
	, SYSDATE
	, #{memberBirth}
	, #{gender}
	, #{memberEmail}
	, null
	, 0
)
